import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';
import { CalendarOptions } from '@fullcalendar/core';
import  dayGridPlugin from '@fullcalendar/daygrid';
import  timeGridPlugin  from '@fullcalendar/daygrid';
import  interactionPlugin  from '@fullcalendar/daygrid';
import  listPlugin  from '@fullcalendar/daygrid';
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss']
})
export class AppComponent implements OnInit {
  title = 'MadrastyUI';
 public deps : any[] = [];
 calendarOptions: CalendarOptions = {
  plugins: [dayGridPlugin,timeGridPlugin,interactionPlugin,listPlugin],
  initialView: 'dayGridMonth',
  weekends: true,
  events: [
    { title: 'Meeting', start: new Date() },
    { title: 'Meeting2', start: new Date() },
    { title: 'Meeting3', start: new Date() },
    { title: 'Meeting4', start: new Date() },
    { title: 'Meeting5', start: new Date() },
    { title: 'Meeting6', start: new Date() },
  ]
};
 model = {
  dep_name:'',
  dep_desc:'',
  dep_supervisor_id:0,
  parent_id: 0,
  dep_supervisor_name:''
 }

 model2 = {
  dep_name:'',
  dep_desc:'',
  dep_supervisor_id:0,
  parent_id: 0,
  dep_supervisor_name:''
 }
  constructor(private http: HttpClient) {
  }

  ngOnInit(): void {
    //this.getDepartments();
  }

  resetForm(){
    this.model = {
      dep_name:'',
      dep_desc:'',
      dep_supervisor_id:0,
      parent_id: 0,
      dep_supervisor_name:''
     }
  }

  resetForm2(){
    this.model2 = {
      dep_name:'',
      dep_desc:'',
      dep_supervisor_id:0,
      parent_id: 0,
      dep_supervisor_name:''
     }

     document.getElementById('modalCloser')?.click();
  }

  loadModel(depId:number,openModal:boolean){
    this.deps.forEach((d)=>{
      if(d.dep_id == depId){
        this.model = d;
        this.model2 = d;
        if(openModal){
          document.getElementById('modalCloser')?.click();
        }
      }
    });
  }

  submitForm(){
    this.http.post("https://localhost:44337/api/department",this.model).subscribe(
      {
        next: (result : any)=>{
          this.getDepartments();
          this.resetForm();
        },
        error: (err)=>{
          console.log(err);
        }
      }
    )
  }

  submitForm2(){
    this.http.post("https://localhost:44337/api/department",this.model2).subscribe(
      {
        next: (result : any)=>{
          this.getDepartments();
          this.resetForm2();
        },
        error: (err)=>{
          console.log(err);
        }
      }
    )
  }

  getDepartments(){
    this.http.get("https://localhost:44337/api/department").subscribe(
      {
        next: (result : any)=>{
          this.deps = result;
        },
        error: (err)=>{
          console.log(err);
        }
      }
    )
  }
}
