import { HttpClient } from '@angular/common/http';
import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-department',
  templateUrl: './department.component.html',
  styleUrls: ['./department.component.scss']
})
export class DepartmentComponent implements OnInit {

  public deps : any[] = [];
  model = {
   dep_name:'',
   dep_desc:'',
   dep_supervisor_id:0,
   parent_id: 0,
   dep_supervisor_name:''
  }

  /**
   *
   */
  constructor(private http:HttpClient,public layoutService: LayoutService) {
    layoutService.subHeaderTitle = 'Departments';
  }

  ngOnInit(): void {

  }

  resetForm(){
    this.model = {
      dep_name:'',
      dep_desc:'',
      dep_supervisor_id:0,
      parent_id: 0,
      dep_supervisor_name:''
     }
  }

  submitForm(){
    this.http.post("https://localhost:44337/api/department",this.model).subscribe(
      {
        next: (result : any)=>{
          this.getDepartments();
          this.resetForm();
        },
        error: (err)=>{
          console.log(err);
        }
      }
    )
  }

  getDepartments(){
    this.http.get("https://localhost:44337/api/department").subscribe(
      {
        next: (result : any)=>{
          this.deps = result;
        },
        error: (err)=>{
          console.log(err);
        }
      }
    )
  }

}
